async function getWebsocketUrl () {
    return new Promise(async (resolve, reject) => {
        var apiKey = API_KEY
        var apiSecret = API_SECRET
        var url = `wss://spark-api.xf-yun.com/${version}/chat`
        var host = location.host
        var date = new Date().toGMTString()
        var algorithm = 'hmac-sha256'
        var headers = 'host date request-line'
        var signatureOrigin = `host: ${host}\ndate: ${date}\nGET /${version}/chat HTTP/1.1`
        var signatureSha = CryptoJS.HmacSHA256(signatureOrigin, apiSecret);
        var signature = CryptoJS.enc.Base64.stringify(signatureSha);
        var authorizationOrigin = `api_key="${apiKey}", algorithm="${algorithm}", headers="${headers}", signature="${signature}"`
        var authorization = btoa(authorizationOrigin)
        url = `${url}?authorization=${authorization}&date=${date}&host=${host}`
        resolve(url)
    })
}
class TTSRecorder {
    constructor({
        appId = APPID
    } = {}) {
        this.appId = appId
        this.status = 'init'
    }
    // ä¿®æ”¹çŠ¶æ€
    setStatus (status) {
        this.onWillStatusChange && this.onWillStatusChange(this.status, status)
        this.status = status
    }
    // è¿æ¥websocket
    connectWebSocket () {
        this.setStatus('ttsing')
        return getWebsocketUrl().then(url => {
            let ttsWS
            if ('WebSocket' in window) {
                ttsWS = new WebSocket(url)
            } else if ('MozWebSocket' in window) {
                ttsWS = new MozWebSocket(url)
            } else {
                alert('æµè§ˆå™¨ä¸æ”¯æŒWebSocket')
                return
            }
            this.ttsWS = ttsWS
            ttsWS.onopen = e => {
                this.webSocketSend()
            }
            ttsWS.onmessage = e => {
                this.result(e.data)
            }
            ttsWS.onerror = e => {
                clearTimeout(this.playTimeout)
                this.setStatus('error')
                alert('WebSocketæŠ¥é”™ï¼Œè¯·f12æŸ¥çœ‹è¯¦æƒ…')
                console.error(`è¯¦æƒ…æŸ¥çœ‹ï¼š${encodeURI(url.replace('wss:', 'https:'))}`)
            }
            ttsWS.onclose = e => {
            }
        })
    }
    // websocketå‘é€æ•°æ®
    webSocketSend () {
        this.ttsWS.send(JSON.stringify(params))
    }
    start () {
        total_res = ""; // è¯·ç©ºå›ç­”å†å²
        this.connectWebSocket()
    }
    // websocketæ¥æ”¶æ•°æ®çš„å¤„ç†
    result (resultData) {
        let jsonData = JSON.parse(resultData)
        total_res += jsonData.payload.choices.text[0].content
        if (jsonData.header.status == 0) {
            let chatBox = document.createElement('div')
            let dom = `

            ${total_res}
            `
            chatBox.innerHTML = dom
            chatBox.classList.add('chatpdfRow','assistant')
            chatpdfLine.appendChild(chatBox)
        }
        var lastChild = chatpdfLine.lastElementChild;
        lastChild.children[1].textContent = total_res
        chatpdfLine.scrollTop = chatpdfLine.scrollHeight;
        // æé—®å¤±è´¥
        if (jsonData.header.code !== 0) {
            alert(`æé—®å¤±è´¥: ${jsonData.header.code}:${jsonData.header.message}`)
            console.error(`${jsonData.header.code}:${jsonData.header.message}`)
            return
        }
        if (jsonData.header.code === 0 && jsonData.header.status === 2) {
            params.payload.message.text.push({
                "role": "assistant",
                "content": total_res
            })
            this.ttsWS.close()
            this.setStatus("init")
        }
    }
}
let bigModel = new TTSRecorder()
