    const currentDate = new Date(); // å½“å‰æ—¶é—´
    const diffInMilliseconds = Math.abs(targetDate - currentDate); // è®¡ç®—å·®å€¼(å•ä½ä¸ºæ¯«ç§’)
    const date = new Date(diffInMilliseconds); // åˆ›å»ºä¸€ä¸ªè¡¨ç¤ºæŒ‡å®šæ—¶é—´çš„Dateå¯¹è±¡
    time.diffInDays = Math.floor(date.getTime() / (24 * 60 * 60 * 1000)); // è®¡ç®—å¤©æ•°å·®å€¼
    time.diffInHours = Math.floor(
        (date.getTime() % (24 * 60 * 60 * 1000)) / (60 * 60 * 1000)
    ); // è®¡ç®—å°æ—¶å·®å€¼
    time.diffInMinutes = Math.floor(
        (date.getTime() % (60 * 60 * 1000)) / (60 * 1000)
    ); // è®¡ç®—åˆ†é’Ÿå·®å€¼
    time.diffInSeconds = Math.floor(
        (date.getTime() % (60 * 1000)) / 1000
    ); // è®¡ç®—ç§’å·®å€¼
    return time
}
