    .chatpdfAsk {
        justify-content: flex-end;
        position: relative;
        padding-right: 25px;
    }

    .chatpdfAsk img {
        position: absolute;
        right: 0;
        top: 0;
    }
    .chatpdfContent {
        margin: 0;
        white-space: pre-wrap;
        border-radius: 8px;
        max-width: 700px;
        box-shadow: 0px 0.3px 0.9px rgba(0, 0, 0, 0.12), 0px 1.6px 3.6px rgba(0, 0, 0, 0.16);
        min-height: 25px;
        box-sizing: border-box;
        padding: 10px;
        line-height: 1.8;
    }

    .assistant .chatpdfContent {
        margin-left: 10px;
        background: var(--textbackground);
        color: var(--textcolor);
    }

    .chatpdfAsk .chatpdfContent {
        margin-right: 10px;
        background: var(--userbackground);
        color: var(--usertextcolor);
    }

    .chatpdfArea {
        display: flex;
        padding: 10px;
        width: 80%;
        margin: 0 auto;
        position: relative;
        box-sizing: border-box;
    }

    .chatpdfArea textarea {
        flex: 1;
        outline: none;
        border: none;
        padding: 10px;
        min-height: 40px;
        max-height: 250px;
        border-radius: 8px;
        box-sizing: border-box;
        transition: all 0.3s, height 0.2s;
        background: var(--textbackground);
        color: var(--textcolor);
        font-size: 15px;
        font-family: 'JetBrains Mono';
    }

    .chatpdfArea textarea::-webkit-scrollbar {
        display: none
    }

    .chatpdfArea button {
        position: absolute;
        height: 35px;
        background: transparent;
        border: none;
        padding: 0 20px;
        border-radius: 8px;
        right: 20px;
        bottom: 10px;
        cursor:not-allowed
    }








