* {
    margin: 0;
    padding: 0;
}
.chatpdf {
    display: flex;
    height: 100%;
    flex-direction: column;
    flex: 1;
}
.chatpdfBox {
    height: 100%;
    display: flex;
    flex-direction: column;
    flex: 1;
}
.chatpdfLine {
    flex: 1;
    width: 100%;
    max-width: 1000px;
    margin: 0 auto;
    overflow-y: auto;
}
.chatpdfRow {
    margin: 20px 10px;
    display: flex;
}
.chatpdfAsk {
    justify-content: flex-end;
}
.chatpdfContent {
    display: inline-block;
    border-radius: 8px;
    padding: 6px 12px;
    max-width: 500px;
    font-size: 14px;
    background: rgb(68, 70, 84)
    color: rgb(255,255,255);
    box-shadow: 0px 0.3px 0.9px rgba(0, 0, 0, 0.12), 0px 1.6px 3.6px rgba(0, 0, 0, 0.16);
}
.chatpdfAsk .chatpdfContent {
    background: linear-gradient(90deg, #5b5b8a 10.79%, rgb(52, 53, 65) 87.08%);
    color: #fff;
}
.chatpdfArea {
    display: flex;
    padding: 5px 10px;
    max-width: 1000px;
    margin: 0 auto;
    width: 100%;
    position: relative;
}
.chatpdfArea textarea {
    flex: 1;
    /* resize: vertical; */
    outline: none;
    border-color:rgb(58, 58, 60);
    padding: 5px;
    min-height: 40px;
    height: auto;
    color: #eee;
    border-radius: 8px;
    box-sizing: border-box;
    transition: all 0.3s, height 0s;
    background: rgb(58, 58, 60);
    max-height: 200px;
}
.chatpdfArea textarea::-webkit-scrollbar {display:none}

.chatpdfArea textarea:hover {
    border-color: rgb(28, 29, 30);
}
.chatpdfArea button {
    position: absolute;
    height: 35px;
    color: #fff;
    background: rgb(58, 58, 60);
    border: none;
    padding: 0 20px;
    border-radius: 8px;
    cursor: pointer;
    right: 20px;
    bottom: 8px;
}
.chatpdfArea button:hover {
    /* background-color: #388aff; */
}
.chatpdf .fileTitle {
    background-color: #1677ff;
    color: #fff;
    border-radius: 8px;
    padding: 10px;
    margin: 10px;
    font-size: 14px;
    cursor: pointer;
}
@media (max-width: 768px) {
    .pannel {
        display: none;
    }
}





